"use strict";document.addEventListener("DOMContentLoaded",(()=>{document.querySelector(".prev"),document.querySelector(".next");let e=document.querySelector(".box"),t=270;e.addEventListener("click",(r=>{r.target.className.includes("prev")?(t+=90,e.style=`transform: perspective(1000px) rotateY(${t+-25}deg)`):r.target.className.includes("next")&&(t-=90,e.style=`transform: perspective(1000px) rotateY(${t+-25}deg)`)}))}));